import React, { useState, useEffect } from "react";
import axios from "axios";

const Movies = () => {
  const[page,setPage] = useState(1);
  const [content,setContent] = useState([]);
  const [numOfPages,setNumOfPages] = useState();
  const fetchMovies = async () => {
    const { data } = await axios.get(
      `https://api.themoviedb.org/3/discover/movie?api_key=67fefbaa38331d1b02df78df2fb49196&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&${page}`
    );
    setContent(data.results);
    setNumOfPages(data.total_pages);
  };

  useEffect(() => {
     fetchMovies();
  }, [])
  return (
    <div>
      <span className="page-title">Movies</span>
    </div>
  );
};

export default Movies;
